    morphism/.style={draw, rectangle},
    over/.style={
      draw=white,
      double=black,
      % default is .4pt
      line width=1.2pt,
      double distance=.4pt,
      text=black
    },
  }

  \tikzset{ 
    % tables
    % https://tikz.net/tikz-table/
    table/.style={
        matrix of nodes,
        row sep=-\pgflinewidth,
        column sep=-\pgflinewidth,
        nodes={
            rectangle,
            draw=black,
            align=center
        },
        minimum height=1.5em,
        text depth=0.5ex,
        text height=2ex,
        nodes in empty cells,
%%
        every even row/.style={
            nodes={fill=gray!20}
        },
        column 1/.style={
            nodes={font=\bfseries}
        },
        row 1/.style={
            nodes={
                fill=black,
                text=white,
                font=\bfseries
            }
        }
    }
  }


      \begin {tikzcd}
        \alpha \ar [dd, mapsto, "(f_*)^*"']\ar [r, mapsto, "\Phi _A"] & \alpha _A(1_A)\ar [d, "Ff"] \\
                                                                & Ff(\alpha _c(1_c))\\
        \alpha  f_*\ar [r, mapsto, "\Phi _B"'] & (\alpha  f_*)_B(1_B)\ar [u, equals]\makebox [0pt][l]{,}
      \end {tikzcd}


where the equality is exactly the final step of the bijectivity proof.

