    \\
    Fc\ar [r, hook] &
    \displaystyle \prod _{c\xrightarrow {j} x} (F\Pi  j = Fx)\ar [r, shift left=.2em, dashed]\ar [r, shift
    right=.2em, dashed]\ar [ur, "\pi _{kj}"]\ar [d, "\pi _j"'] &
    \displaystyle \prod _{c\xrightarrow {j} x\xrightarrow {k} y} (F\Pi  (kj) = Fy)\ar [u, "\pi _{kj}"']\ar [d, "\pi _{kj}"]\makebox [0pt][l]{.} \\
    & Fx\ar [r, "Fk"'] & Fy
    \end {tikzcd}

