    \usepackage{tikz, tikz-cd, mathtools, amssymb}
  \usetikzlibrary{shapes.geometric}
  \usetikzlibrary{calc}
  \usetikzlibrary{matrix}
  \usetikzlibrary{decorations.markings}

  \tikzset{
    % basics
    square/.style={draw, regular polygon, regular polygon sides=4},
    triangle/.style={draw, regular polygon, regular polygon sides=3},
  }

  \tikzset{
    % string diagrams
    state/.style={triangle, shape border rotate=180, anchor=north},
    costate/.style={triangle, anchor=south},
    morphism/.style={draw, rectangle},
    over/.style={
      draw=white,
      double=black,
      % default is .4pt
      line width=1.2pt,
      double distance=.4pt,
      text=black
    },
  }

  \tikzset{ 
    % tables
    % https://tikz.net/tikz-table/
    table/.style={
        matrix of nodes,
        row sep=-\pgflinewidth,
        column sep=-\pgflinewidth,
        nodes={
            rectangle,
            draw=black,
            align=center
        },
        minimum height=1.5em,
        text depth=0.5ex,
        text height=2ex,
        nodes in empty cells,
%%
        every even row/.style={
            nodes={fill=gray!20}
        },
        column 1/.style={
            nodes={font=\bfseries}
        },
        row 1/.style={
            nodes={
                fill=black,
                text=white,
                font=\bfseries
            }
        }
    }
  }

  \begin {tikzcd}
			C\ar [rr, "{C(c, -)}"] && \textsc {Set} \\
			x\ar [rr, mapsto]\ar [dd, "f"'] && C(c, x)\ar [dd, "f_*"] && c\ar [rr, "g"] &\ar [dd, mapsto] & x\\
			\ar [rr, mapsto] && \hspace {0em}\\
			y\ar [rr, mapsto] && C(c, y) && c\ar [r, "g"] & x\ar [r, "f"] & y\makebox [0pt][l]{.}
  \end {tikzcd}


