\begin {tikzcd}
  x\ar [r, "f"]\ar [rr, "gf", bend left=45] & y\ar [r, "g"]\ar [rr, "hg"', bend right=45] & z\ar [r, "h"] & w
\end {tikzcd}

\begin {tikzcd}
  x\ar [r, "1_x"]\ar [dr, "f"'] & x\ar [d, "f"] \\
  & y
\end {tikzcd}
\begin {tikzcd}
  x\ar [r, "f"]\ar [dr, "f"'] & y\ar [d, "1_y"] \\
  & y\makebox [0pt][l]{.}
\end {tikzcd}
