  \begin {tikzcd}
    C^\text {op}\ar [r, "\operatorname {Sub}_C"] & \textsc {Set} \\
    x\ar [r, mapsto] & \operatorname {Sub}_C(x)\ar [d, "f^*", ""' {name=f*}] & x & s\ar [l, hook] \\
    y\ar [u, "f", ""' {name=f}]\ar [r, mapsto] & \operatorname {Sub}_C(y) & y\ar [u, "f"] &
    f^*s\makebox [0pt][l]{.}\ar [l, hook]\ar [u]\ar [ul, phantom, "\ulcorner ", very near start]
    \ar [from=f, to=f*, mapsto]
  \end {tikzcd}

