    (x\otimes  y)\otimes  z\ar [r, "\alpha _{xyz}"]\ar [d, "\gamma _{xy}\otimes  1"'] &
    x\otimes (y\otimes  z)\ar [r, "\gamma _{x,y\otimes  z}"] & (y\otimes  z)\otimes 
    x\ar [d, "\alpha _{yzx}"]\\
    (y\otimes  x)\otimes  z\ar [r, "\alpha _{yxz}"'] & y\otimes  (x\otimes  z)\ar [r,
    "1\otimes  \gamma _{xz}"'] & y\otimes (z\otimes  x)
  \end {tikzcd}


