      left]\ar [uurr, "" {name=G, above}, "G"', bend right]
      \ar [Rightarrow, from=3-2, to=1-2, "\epsilon ", shorten >= 1em, shorten <= 1em,
      xshift=-.2em, yshift=.4em]
      \ar [Rightarrow, dashed, from=G, to=Ran, "\exists !", shorten >= .5em]
    \end {tikzcd}

