    y\ar [u, "f", ""' {name=f}]\ar [r, mapsto] & \operatorname {Sub}_C(y) & y\ar [u, "f"] &
    f^*s\makebox [0pt][l]{.}\ar [l, hook]\ar [u]\ar [ul, phantom, "\ulcorner ", very near start]
