      & & F'\ar [dr, "F'\eta "']\ar [drdr, "\theta '", bend left] \\
      & FGF'\ar [ur, "\epsilon  F'"']\ar [dr, "FGF'\eta "] & & F'GF\ar [dr, "\epsilon  F"'] \\
      F\ar [ruru, "\theta ", bend left]\ar [rrrr, "1_F"', bend right=100]\ar [ur,
