  }


		\begin {tikzcd}
			(x\otimes  I)\otimes  y\ar [rr, "\alpha _{x,1_\otimes ,y}"]\ar [dr, "\rho _x"']
      & & x\otimes (I\otimes  y)\ar [dl, "\lambda _x"] \\
			& x\otimes  y\makebox [0pt][l]{;}
		\end {tikzcd}









