    \usepackage{tikz, tikz-cd, mathtools, amssymb}
  \usetikzlibrary{shapes.geometric}
  \usetikzlibrary{calc}
  \usetikzlibrary{matrix}
  \usetikzlibrary{decorations.markings}

  \tikzset{
    % basics
    square/.style={draw, regular polygon, regular polygon sides=4},
    triangle/.style={draw, regular polygon, regular polygon sides=3},
  }

  \tikzset{
    % string diagrams
    state/.style={triangle, shape border rotate=180, anchor=north},
    costate/.style={triangle, anchor=south},
    morphism/.style={draw, rectangle},
    over/.style={
      draw=white,
      double=black,
      % default is .4pt
      line width=1.2pt,
      double distance=.4pt,
      text=black
    },
  }

  \tikzset{ 
    % tables
    % https://tikz.net/tikz-table/
    table/.style={
        matrix of nodes,
        row sep=-\pgflinewidth,
        column sep=-\pgflinewidth,
        nodes={
            rectangle,
            draw=black,
            align=center
        },
        minimum height=1.5em,
        text depth=0.5ex,
        text height=2ex,
        nodes in empty cells,
%%
        every even row/.style={
            nodes={fill=gray!20}
        },
        column 1/.style={
            nodes={font=\bfseries}
        },
        row 1/.style={
            nodes={
                fill=black,
                text=white,
                font=\bfseries
            }
        }
    }
  }

\begin {tikzcd}
  x\ar [r, "f"]\ar [rr, "gf", bend left=45] & y\ar [r, "g"]\ar [rr, "hg"', bend right=45] & z\ar [r, "h"] & w
\end {tikzcd}

\begin {tikzcd}
  x\ar [r, "1_x"]\ar [dr, "f"'] & x\ar [d, "f"] \\
  & y
\end {tikzcd}
\begin {tikzcd}
  x\ar [r, "f"]\ar [dr, "f"'] & y\ar [d, "1_y"] \\
  & y\makebox [0pt][l]{.}
\end {tikzcd}
