\begin {tikzcd}
   C(y, y)\otimes  C(x, y) &  C(x, y) &  C(x, y)\otimes  C(x, x) \\
  1_\otimes \otimes  C(x, y) & &  C(x, y)\otimes  1_\otimes \makebox [0pt][l]{.}
  \arrow ["\circ _{x, y, y}", from=1-1, to=1-2]
  \arrow ["\circ _{x, x, y}", from=1-3, to=1-2, swap]
  \arrow ["1_y\otimes 1_{ C(x, y)}", from=2-1, to=1-1]
  \arrow ["\lambda _{C(x, y)}", from=2-1, to=1-2, swap]
  \arrow ["\rho _{C(x, y)}", from=2-3, to=1-2]
  \arrow ["1_{ C(x, y)}\times 1_x", from=2-3, to=1-3, swap]
\end {tikzcd}


