  \begin {tikzcd}
    x\ar [dr, "\iota _x"] && x \\
    & x\oplus  y\ar [ur, "\pi _x"]\ar [dr, "\pi _y"'] \\
    y\ar [ur, "\iota _y"'] && y
  \end {tikzcd}


