    \begin {tikzcd}
    \displaystyle \coprod _{y\to  x\to  c} Fy\ar [r, shift left=.2em]\ar [r, shift right=.2em] &
    \displaystyle \coprod _{x\to  c} Fx\ar [r, two heads] & Fc\makebox [0pt][l]{.}
    \end {tikzcd}
