    costate/.style={triangle, anchor=south},
    morphism/.style={draw, rectangle},
    over/.style={
      draw=white,
      double=black,
      % default is .4pt
      line width=1.2pt,
      double distance=.4pt,
      text=black
    },
  }

  \tikzset{ 
    % tables
